type Snap struct {
	HTML   string `json:"html"`
	Height int
}

func snapHandler(tpl *template.Template) func(w http.ResponseWriter, r *http.Request) {
	return func(w http.ResponseWriter, r *http.Request) {
		snapURL := r.FormValue("snapURL")
		if snapURL == "" {
			handleError(w, errors.New("snapURL query parameter empty"))
			return
		}

		resp, err := http.Get(snapURL)
		if err != nil {
			handleError(w, err)
			return
		}
		defer resp.Body.Close()
		body, err := ioutil.ReadAll(resp.Body)
		if err != nil {
			handleError(w, err)
			return
		}

		var snap Snap
		err = json.Unmarshal(body, &snap)
		if err != nil {
			handleError(w, err)
			return
		}

		snap.Height = 300

		tpl.Execute(w, snap)
	}
}

func handleError(w http.ResponseWriter, err error) {
	w.WriteHeader(500)
	w.Write([]byte(err.Error()))
	return
}



